import react, { useState } from 'react';


function ChangeColor() {
    const [color, setColor] = useState("red");
    const changeColor = () => {
        setColor("green");
    };

    return (
        <div>
            <div onClick={changeColor} style={{ backgroundColor: color }, { width: 200 }}>click me</div>
        </div>
    );
}

export default ChangeColor;
